import ActiveRouter from '../../global/active-router';
/**
  * Updates the document title when found.
  *
  * @name RouteTitle
  * @description
 */
export class RouteTitle {
    constructor() {
        this.titleSuffix = '';
        this.title = '';
    }
    componentWillLoad() {
        document.title = `${this.title}${this.titleSuffix || ''}`;
    }
    static get is() { return "stencil-route-title"; }
    static get properties() { return {
        "el": {
            "elementRef": true
        },
        "title": {
            "type": String,
            "attr": "title"
        },
        "titleSuffix": {
            "type": String,
            "attr": "title-suffix"
        }
    }; }
}
ActiveRouter.injectProps(RouteTitle, [
    'titleSuffix',
]);
